// src/components/ProtectedRoute.js
import React from 'react';
import { Route, Navigate } from 'react-router-dom'; // Updated from Redirect to Navigate

const ProtectedRoute = ({ element: Component, roleRequired, ...rest }) => {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  return (
    <Route
      {...rest}
      element={
        token && role === roleRequired ? (
          <Component />
        ) : (
          <Navigate to="/login" /> // Updated from Redirect to Navigate
        )
      }
    />
  );
};

export default ProtectedRoute;
