// src/components/EmployeeDashboard.js
import React from 'react';

const EmployeeDashboard = () => {
  const token = localStorage.getItem('token');

  if (!token) {
    return <p>You must be logged in to view this page.</p>;
  }

  // Mock data for courses
  const courses = [
    { id: 1, title: 'Introduction to React', progress: 60 },
    { id: 2, title: 'Advanced JavaScript', progress: 30 },
    { id: 3, title: 'Web Security Fundamentals', progress: 0 },
  ];

  return (
    <div>
      <h1>Employee Dashboard</h1>
      <h2>Your Courses</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            {course.title} - Progress: {course.progress}%
            <progress value={course.progress} max="100" />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeDashboard;
