// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import EmployeeDashboard from './components/EmployeeDashboard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        {/* Use ProtectedRoute as a conditionally rendered element */}
        <Route 
          path="/admin-dashboard" 
          element={<ProtectedRoute element={<AdminDashboard />} roleRequired="lcd_admin" />} 
        />
        <Route 
          path="/employee-dashboard" 
          element={<ProtectedRoute element={<EmployeeDashboard />} roleRequired="employee" />} 
        />
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="*" element={<h1>404 Not Found</h1>} /> {/* Optional fallback route */}
      </Routes>
    </Router>
  );
}

export default App;
