import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <div className="animated-background"></div>
      <div className="content">
        <h1 className="title">Elevate Your Career</h1>
        <p className="subtitle">Unlock your potential with our cutting-edge learning platform</p>
        <div className="features">
          <div className="feature">
            <i className="fas fa-rocket"></i>
            <h3>Accelerate Growth</h3>
          </div>
          <div className="feature">
            <i className="fas fa-brain"></i>
            <h3>Expand Knowledge</h3>
          </div>
          <div className="feature">
            <i className="fas fa-chart-line"></i>
            <h3>Track Progress</h3>
          </div>
        </div>
        <div className="auth-options">
          <Link to="/login" className="auth-button login">Get Started</Link>
          <Link to="/register" className="auth-button register">Join Now</Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
